css:
.qr-code{
    float:right;
}

.address-div {
    line-height: normal;
}

.invoice-heading {
  text-align: center;
  margin: 0;
}

.ksa-invoice-table {
  border: 1px solid #888a8e;
  border-collapse: collapse;
  width: 100%;
  margin: 20px 0;
  color: #888a8e;
  font-size: 16px;
}

.ksa-invoice-table.two-columns td:nth-child(2) {
  direction: rtl;
}

.ksa-invoice-table th {
  border: 1px solid #888a8e;
  max-width: 50%;
  background-color: #265e4a !important;
  color: #fff;
  padding: 8px;
}

.ksa-invoice-table td {
  padding: 5px;
  border: 1px solid #888a8e;
  max-width: 50%;
}

.ksa-invoice-table thead,
.ksa-invoice-table tfoot {
  text-transform: uppercase;
}

.rtl {
  direction: rtl;
}
